import React, { useEffect, useState } from 'react'
import Navigation from '../components/Navigation';
import Footer from '../components/Footer';
import { readRceta } from '../core/apiCore';


function VerReceta(props) {

    const [receta, setReceta] = useState([]);
    const [error, setError] = useState(false);

    const loadUnaReceta = recetaId => {
        readRceta(recetaId).then(data => {
            if (data.error) {
                setError(data.error);
            } else {
                setReceta(data);
            }
        });
    }

    useEffect(() => {
        const recetaId = props.match.params.recetaId;
        loadUnaReceta(recetaId)
    }, [props])

    return (
        <div>
            <Navigation />


            <Footer />

        </div>
    )
}

export default VerReceta
