import React, { useEffect, useState } from 'react'
import Navigation from '../components/Navigation';
import Footer from '../components/Footer';
import ReactPlayer from 'react-player';

import { readReceta } from '../core/apiCore';

import { API } from '../config';


function VerReceta(props) {

    const [receta, setReceta] = useState({});
    const [error, setError] = useState(false);

    const loadUnaReceta = recetaId => {
        readReceta(recetaId).then(data => {
            if (data.error) {
                setError(data.error);
            } else {
                setReceta(data);
            }
        });
    }

    useEffect(() => {
        const recetaId = props.match.params.recetaId;
        loadUnaReceta(recetaId)
        console.log(receta);
    }, [props])

    return (
        <div>
            <Navigation />
            <div className="grid md:grid-flow-col flex container m-5">
                <div className='rounded-lg'>
                    <div className="">
                        <img
                            src={`${API}/receta/photo/${receta._id}`}
                            alt={receta.name}
                            className="mb-3 img-cont rounded-lg"
                            style={{ maxHeight: "600px", maxWidth: "300px" }} />
                        <ReactPlayer
                            url={receta.video}
                            style={{ maxWidth: '300px' }}
                        />
                    </div>

                </div>
                <div className='text-center items-center'>
                    <h1 className='text-7xl uppercase font-black text-black m-4' >Ingredientes</h1>
                    <div className="bg-black m-6 rounded-lg text-white text-2xl p-4">
                        {/* {receta.ingredientes} */}

                        {receta.ingredientes.split('#').map((step) => <h2>{step}{",\n"}</h2>)}
                    </div>
                </div>
            </div>


            <Footer />

        </div>
    )
}

export default VerReceta
