import React from 'react'
import ShowImage from './ShowImage';
import { Link } from 'react-router-dom';
// import 'bootstrap/dist/css/bootstrap.min.css';

function RecetaCard({ receta }) {
    return (
        <div>
            <div className="rounded-lg shadow-lg bg-gray-600 w-full flex flex-row flex-wrap p-3 antialiased" >
                <div className="md:w-1/3 w-full">

                    <ShowImage className='img' item={receta} url='receta' />
                </div>
                <div className="md:w-2/3 w-full px-3 flex flex-row flex-wrap">
                    <div className="w-full text-right text-gray-700 font-semibold relative pt-3 md:pt-0">
                        <div className="text-2xl text-white leading-tight">{receta.name}</div>

                        <div className="text-normal text-gray-300 hover:text-gray-400 cursor-pointer"><Link to={`/verreceta/${receta._id}`} className="border-b border-dashed border-gray-500 pb-1">Ver Receta</Link></div>
                        {/* <div className="text-sm text-gray-300 hover:text-gray-400 cursor-pointer md:absolute pt-3 md:pt-0 bottom-0 right-0">Last Seen: <b>2 days ago</b></div> */}
                    </div>
                </div>
            </div>
        </div>
    )
}

export default RecetaCard
