import React, { useEffect, useState } from 'react'
import Navigation from '../components/Navigation';
import Footer from '../components/Footer';
import ShowImage from '../components/Cards/ShowImage';

import { readReceta } from '../core/apiCore';


function VerReceta(props) {

    const [receta, setReceta] = useState({});
    const [error, setError] = useState(false);

    const loadUnaReceta = recetaId => {
        readReceta(recetaId).then(data => {
            if (data.error) {
                setError(data.error);
            } else {
                setReceta(data);
            }
        });
    }

    useEffect(() => {
        const recetaId = props.match.params.recetaId;
        loadUnaReceta(recetaId)
        console.log(receta);
    }, [props])

    return (
        <div>
            <Navigation />
            <div className="grid md:grid-flow-col flex container m-5">
                <div className='rounded-lg'>
                    <div className="product-img">
                        <img
                            src={`${API}/${url}/photo/${item._id}`}
                            alt={item.name}
                            className="mb-3 img-cont rounded-full h-24"
                            style={{ maxHeight: "600px", maxWidth: "300px" }} />
                    </div>

                </div>
            </div>


            <Footer />

        </div>
    )
}

export default VerReceta
