import React, { useEffect, useState } from 'react'
import Navigation from '../components/Navigation';
import Footer from '../components/Footer';
import ShowImage from './ShowImage';

import { readReceta } from '../core/apiCore';


function VerReceta(props) {

    const [receta, setReceta] = useState({});
    const [error, setError] = useState(false);

    const loadUnaReceta = recetaId => {
        readReceta(recetaId).then(data => {
            if (data.error) {
                setError(data.error);
            } else {
                setReceta(data);
            }
        });
    }

    useEffect(() => {
        const recetaId = props.match.params.recetaId;
        loadUnaReceta(recetaId)
        console.log(receta);
    }, [props])

    return (
        <div>
            <Navigation />
            <div className="grid md:grid-flow-col flex">
                <ShowImage className='img' item={receta} url='receta' />
            </div>


            <Footer />

        </div>
    )
}

export default VerReceta
