import React, { Fragment, useState, useEffect } from 'react';

import { Link } from 'react-router-dom';

import logo from '../Recursos/SVG/jujunegro.svg'
import Dropdown from './Dropdown';

const Navigation = () => {

    const [isOpen, setIsOpen] = useState(false);
    const toggle = () => {
        setIsOpen(!isOpen);
    }

    useEffect(() => {
        const hideMenu = () => {
            if (window.innerWidth > 768 && isOpen) {
                setIsOpen(false);
                console.log('resize');
            }
        }
        window.addEventListener('resize', hideMenu);
        return () => {
            window.removeEventListener('resize', hideMenu);
        };
    })



    return (
        <>
            <nav className='flex justify-between items-center h-16 bg-white text-black relative shadow-sm font-mono'
                role='navigation'>
                <Link to='/' className='pl-8'>

                    <img
                        src={logo}
                        width="50"
                        height="50"
                        className="d-inline-block align-top ms-4"
                        alt="JuJu Coffee"
                    />
                </Link>
                <div className="px-4 cursor-pointer md:hidden"
                    onClick={toggle}
                >
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
                    </svg>
                </div>
                <div className="pr-8 md:block hidden">
                    <Link className="p-4" to='/'>Home</Link>
                    <Link className="p-4" to='/shop'>Shop</Link>
                    <Link className="p-4" to='/about'>About</Link>
                    <Link className="p-4" to='/contact'>Contact</Link>
                </div>
            </nav>
            <Dropdown isOpen={isOpen} toggle={toggle} />
        </>
    )
}

export default Navigation;
